function setup_for_width(a) {
    a.matches ? console.log("The screen width is 1285px or wider.  Set up or change things appropriately.") : console.log("The screen width is less than 1285px.  Set up or change things appropriately.");
}

var SiteTemplate = function() {}, siteTemplate = new SiteTemplate();

$(window).load(function() {});

var width_mql = window.matchMedia("(min-width: 1285px)");

$(function() {
    width_mql.addListener(setup_for_width), setup_for_width(width_mql);
});